create database if not exists tip;

create external table if not exists tip.vehicleinfo_cache
(Id string, Speed int, TimePlate string, HasPlate int, Plate string, PlateColor int, PlateType int, PlatePos_Left int, PlatePos_Top int, PlatePos_Right int, PlatePos_Bottom int, VehicleModel string, VehicleBrand int, VehicleSubModel int, VehicleType int, VehiclePos_Left int, VehiclePos_Top int, VehiclePos_Right int, VehiclePos_Bottom int, ColorCar_0 int, ColorCar_1 int, FaceNum int, FacePos_0_Left int, FacePos_0_Top int, FacePos_0_Right int, FacePos_0_Bottom int, FacePos_1_Left int, FacePos_1_Top int, FacePos_1_Right int, FacePos_1_Bottom int, HasCarWindows int, CarWindowsPos_Left int, CarWindowsPos_Top int, CarWindowsPos_Right int, CarWindowsPos_Bottom int, YearLogoNum int, YearLogo_0_Type int, YearLogo_0_Left int, YearLogo_0_Top int, YearLogo_0_Right int, YearLogo_0_Bottom int, YearLogo_1_Type int, YearLogo_1_Left int, YearLogo_1_Top int, YearLogo_1_Right int, YearLogo_1_Bottom int, YearLogo_2_Type int, YearLogo_2_Left int, YearLogo_2_Top int, YearLogo_2_Right int, YearLogo_2_Bottom int, YearLogo_3_Type int, YearLogo_3_Left int, YearLogo_3_Top int, YearLogo_3_Right int, YearLogo_3_Bottom int, YearLogo_4_Type int, YearLogo_4_Left int, YearLogo_4_Top int, YearLogo_4_Right int, YearLogo_4_Bottom int, SunVisorNum int, SunVisorPos_0_Left int, SunVisorPos_0_Top int, SunVisorPos_0_Right int, SunVisorPos_0_Bottom int, SunVisorPos_1_Left int, SunVisorPos_1_Top int, SunVisorPos_1_Right int, SunVisorPos_1_Bottom int, HasCarPendant int, CarPendantPos_Left int, CarPendantPos_Top int, CarPendantPos_Right int, CarPendantPos_Bottom int, HasDecoration int, DecorationPos_Left int, DecorationPos_Top int, DecorationPos_Right int, DecorationPos_Bottom int, HasMainDriver int, MainDriverPos_Left int, MainDriverPos_Top int, MainDriverPos_Right int, MainDriverPos_Bottom int, MainSafetyBelt int, HasViceDriver int, ViceDriverPos_Left int, ViceDriverPos_Top int, ViceDriverPos_Right int, ViceDriverPos_Bottom int, ViceSafetyBelt int, ImageSize int, DeviceUniqueCode string) ROW FORMAT SERDE 'org.apache.hadoop.hive.hbase.HBaseSerDe' STORED BY 'org.apache.hadoop.hive.hbase.HBaseStorageHandler' WITH SERDEPROPERTIES ('hbase.columns.mapping' = ':key,cf:Speed,cf:TimePlate,cf:HasPlate,cf:Plate,cf:PlateColor,cf:PlateType,cf:PlatePos_Left,cf:PlatePos_Top,cf:PlatePos_Right,cf:PlatePos_Bottom,cf:VehicleModel,cf:VehicleBrand,cf:VehicleSubModel,cf:VehicleType,cf:VehiclePos_Left,cf:VehiclePos_Top,cf:VehiclePos_Right,cf:VehiclePos_Bottom,cf:ColorCar_0,cf:ColorCar_1,cf:FaceNum,cf:FacePos_0_Left,cf:FacePos_0_Top,cf:FacePos_0_Right,cf:FacePos_0_Bottom,cf:FacePos_1_Left,cf:FacePos_1_Top,cf:FacePos_1_Right,cf:FacePos_1_Bottom,cf:HasCarWindows,cf:CarWindowsPos_Left,cf:CarWindowsPos_Top,cf:CarWindowsPos_Right,cf:CarWindowsPos_Bottom,cf:YearLogoNum,cf:YearLogo_0_Type,cf:YearLogo_0_Left,cf:YearLogo_0_Top,cf:YearLogo_0_Right,cf:YearLogo_0_Bottom,cf:YearLogo_1_Type,cf:YearLogo_1_Left,cf:YearLogo_1_Top,cf:YearLogo_1_Right,cf:YearLogo_1_Bottom,cf:YearLogo_2_Type,cf:YearLogo_2_Left,cf:YearLogo_2_Top,cf:YearLogo_2_Right,cf:YearLogo_2_Bottom,cf:YearLogo_3_Type,cf:YearLogo_3_Left,cf:YearLogo_3_Top,cf:YearLogo_3_Right,cf:YearLogo_3_Bottom,cf:YearLogo_4_Type,cf:YearLogo_4_Left,cf:YearLogo_4_Top,cf:YearLogo_4_Right,cf:YearLogo_4_Bottom,cf:SunVisorNum,cf:SunVisorPos_0_Left,cf:SunVisorPos_0_Top,cf:SunVisorPos_0_Right,cf:SunVisorPos_0_Bottom,cf:SunVisorPos_1_Left,cf:SunVisorPos_1_Top,cf:SunVisorPos_1_Right,cf:SunVisorPos_1_Bottom,cf:HasCarPendant,cf:CarPendantPos_Left,cf:CarPendantPos_Top,cf:CarPendantPos_Right,cf:CarPendantPos_Bottom,cf:HasDecoration,cf:DecorationPos_Left,cf:DecorationPos_Top,cf:DecorationPos_Right,cf:DecorationPos_Bottom,cf:HasMainDriver,cf:MainDriverPos_Left,cf:MainDriverPos_Top,cf:MainDriverPos_Right,cf:MainDriverPos_Bottom,cf:MainSafetyBelt,cf:HasViceDriver,cf:ViceDriverPos_Left,cf:ViceDriverPos_Top,cf:ViceDriverPos_Right,cf:ViceDriverPos_Bottom,cf:ViceSafetyBelt,cf:ImageSize,cf:DeviceUniqueCode') TBLPROPERTIES('hbase.table.name' = 'vehicleinfo_cache');

drop table if exists tip.plate;
create external table tip.plate(plate string,fid string, platetype int, vehicletype int, vehiclebrand int, vehiclesubmodel int, platecolor int, colorcar_0 int, colorcar_1 int, vehiclemodel string, timeplate string, deviceuniquecode string, checked int) ROW FORMAT SERDE 'org.apache.hadoop.hive.hbase.HBaseSerDe' STORED BY 'org.apache.hadoop.hive.hbase.HBaseStorageHandler' WITH SERDEPROPERTIES ('hbase.columns.mapping' = ':key,cf:fid,cf:platetype,cf:vehicletype,cf:vehiclebrand,cf:vehiclesubmodel,cf:platecolor,cf:colorcar_0,cf:colorcar_1,cf:vehiclemodel,cf:timeplate,cf:deviceuniquecode,cf:checked') TBLPROPERTIES('hbase.table.name' = 'tip_plate');
